  display: inline-block;
  color: #f2f2f2;
  text-align: center;
  padding: 5px 5px;
  text-decoration: none;
  font-size: 14px;
`;
const Logo = styled(MenuUrlStyle)`
  float: left;
  font-size: 16px;
  font-weight: bold;
  color: #030303;
  margin-left: 10px;
  margin-right: 10px;
  &:hover  {
    color: #ffffff;
  }
  display: flex;
  align-items: center;
`;
const MainNavClass = styled.div`
  background-color: rgb(255, 102, 0);
  overflow: hidden;
  display: flex;
  align-items: center;
`;
const MOptions = styled(MenuUrlStyle)`
  float: left;
  color: #030303;
`;

