import * as React from 'react'

export const useIsoMorphicEffect =
  typeof window !== 'undefined' ? React.useLayoutEffect : React.useEffect
import * as React from 'react'
import { useIsoMorphicEffect } from './use-iso-morphic-effect'

// We used a "simple" approach first which worked for SSR and rehydration on the client. However we
// didn't take care of the Suspense case. To fix this we used the approach the @reach-ui/auto-id
// uses.
//
// Credits: https://github.com/reach/reach-ui/blob/develop/packages/auto-id/src/index.tsx

let state = { serverHandoffComplete: false }
let id = 0
function generateId() {
  return ++id
}

export function useId() {
  const [id, setId] = React.useState(state.serverHandoffComplete ? generateId : null)

  useIsoMorphicEffect(() => {
    if (id === null) setId(generateId())
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  React.useEffect(() => {
    if (state.serverHandoffComplete === false) state.serverHandoffComplete = true
  }, [])

  return id != null ? '' + id : undefined
}
import * as React from 'react'

export function useIsInitialRender() {
  const initial = React.useRef(true)

  React.useEffect(() => {
    initial.current = false
  }, [])

  return initial.current
}
import * as React from 'react'

export function useIsMounted() {
  const mounted = React.useRef(true)

  React.useEffect(() => {
    return () => {
      mounted.current = false
    }
  }, [])

  return mounted
}
export function match<TValue extends string | number = string, TReturnValue = unknown>(
  value: TValue,
  lookup: Record<TValue, TReturnValue | ((...args: any[]) => TReturnValue)>,
  ...args: any[]
): TReturnValue {
  if (value in lookup) {
    const returnValue = lookup[value]
    return typeof returnValue === 'function' ? returnValue(...args) : returnValue
  }

  const error = new Error(
    `Tried to handle "${value}" but there is no handler defined. Only defined handlers are: ${Object.keys(
      lookup
