    Error.captureStackTrace(error, match)
  }

  throw error
}
export function once<T>(cb: (...args: T[]) => void) {
  const state = { called: false }

  return (...args: T[]) => {
    if (state.called) {
      return
    }
    state.called = true
    return cb(...args)
  }
}
